mapper.insert:
insert into sys_kaptcha (form_id, kaptcha, expire
  )
values (#{formId,jdbcType=VARCHAR}, #{kaptcha,jdbcType=CHAR}, #{expire,jdbcType=TIMESTAMP}
  )

mapper.update:
update sys_kaptcha
set kaptcha = #{kaptcha,jdbcType=CHAR},
  expire = #{expire,jdbcType=TIMESTAMP}
where form_id = #{formId,jdbcType=VARCHAR}

mapper.select:
select form_id, kaptcha, expire
from sys_kaptcha
where form_id = #{formId,jdbcType=VARCHAR}
